class Saadha_InputArray{
	int arr1[],arr2[],arr3[];
	public Saadha_InputArray(int arr1[],int arr2[]) {
		this.arr1=arr1.clone();
		this.arr2=arr2.clone();
		arr3=new int[arr1.length];
		System.out.println(arr1.length);
	}
	
	public double add() {
		double start_saadha=System.nanoTime();
		for(int i=0;i<this.arr1.length;i++) {
			arr3[i]=arr1[i]+arr2[i];
//			System.out.println(arr3[i]);
		}
		double end_saadha=System.nanoTime();
		return end_saadha-start_saadha;
	}
}

